import React, { useState } from 'react'
import Constants from '../utilities/Constants'

export default function PostCreateForm(props) {
    const initialFormData = Object.freeze({
      title: "Post x",
      content: "This is a post and it has some content"
    });
  const [formData, setFormData] = useState(initialFormData)

  const handelChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handelSubmit = (e) => {
    e.preventDefault();

    const postToCreate = {
      postId: 0,
      title: formData.title,
      content: formData.context
    };

    const url = Constants.API_URL_CREATE_POST;

    fetch(url, {
      method: "POST",
      headers: {
        'Content-Type' : 'application/json'
      },
      body: JSON.stringify(postToCreate)
    })
      .then(response => response.json())
      .then(responseFromServer => {
        console.log(responseFromServer);
      })
      .catch(error => {
        console.log(error);
        alert(error);
      });
    props.onPostCreated(postToCreate);
  };

  return (
    <div>
      <form className='w-100 px-5'>
        <h1 className='mt-5'>Create new post</h1>
        <div className='mt-5'>
          <label className='h3 form-label'>Post Title</label>
          <input value={formData.title} name='title' type='text' className='form-control' onChange={handelChange}></input>
        </div>

        <div className='mt-4'>
          <label className='h3 form-label'>Post Content</label>
          <input value={formData.content} name='content' type='text' className='form-control' onChange={handelChange}></input>
        </div>

        <button onClick={handelSubmit} className='btn btn-dark btn-lg w-100 mt-5'>Submit</button>
        <button onClick={() => props.onPostCreated(null)} className='btn btn-secondary btn-lg w-100 mt-3'>Cancel</button>
      </form>
    </div>
  )
};
