import React, { useState } from "react";
import Constants from "../utilities/Constants";

export default function PostUpdateForm(props) {
  const initialFormData = Object.freeze({
    title: props.post.title,
    content: props.post.content
  });
  const [formData, setFormData] = useState(initialFormData);

  const handelChange = e => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handelSubmit = e => {
    e.preventDefault();

    const postToUpdate = {
      postId: props.post.postId,
      title: formData.title,
      content: formData.context
    };

    const url = Constants.API_URL_UPDATE_POST;

    fetch(url, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(postToUpdate)
    })
      .then(response => response.json())
      .then(responseFromServer => {
        console.log(responseFromServer);
      })
      .catch(error => {
        console.log(error);
        alert(error);
      });
    props.onPostUpdated(postToUpdate);
  };

  return (
    <form className="w-100 px-5">
      <h1 className="mt-5">Create new post</h1>
      <div className="mt-5">
        <label className="h3 form-label">Updating the post titled "{props.post.title}".</label>
        <input value={formData.title} name="title"type="text"className="form-control"onChange={handelChange}
        ></input>
      </div>

      <div className="mt-4">
        <label className="h3 form-label">Post Content</label>
        <input value={formData.content} name="content"type="text"className="form-control"onChange={handelChange}></input>
      </div>

      <button onClick={handelSubmit} className="btn btn-dark btn-lg w-100 mt-5">Submit</button>
      <button onClick={() => props.onPostUpdated(null)} className="btn btn-secondary btn-lg w-100 mt-3"
      >
        Cancel
      </button>
    </form>
  );
}
